// import logo from './logo.svg';
// import './App.css';
// Improved React UI with Modern Design & Responsive Styling
// Applying Material-UI Theme & Tailwind CSS for a sleek UI

import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import { CssBaseline, AppBar, Toolbar, Typography, Container, Box } from "@mui/material";
import Login from "./Components/Login";
import Signup from "./Components/Signup";
import Dashboard from "./Components/Dashboard";

const theme = createTheme({
  palette: {
    mode: "light",
    primary: {
      main: "#1976d2",
    },
    secondary: {
      main: "#ff4081",
    },
    background: {
      default: "#f4f6f8",
      paper: "#ffffff",
    },
  },
  typography: {
    fontFamily: "'Poppins', sans-serif",
    h6: {
      fontWeight: 600,
    },
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          textTransform: "none",
        },
      },
    },
  },
});

const PrivateRoute = ({ element }) => {
  const token = localStorage.getItem("token");
  return token ? element : <Navigate to="/" replace />;
};

const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static" sx={{ backgroundColor: "primary.main", boxShadow: 3 }}>
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Academic Platform
          </Typography>
        </Toolbar>
      </AppBar>
      <Box sx={{ minHeight: "90vh", display: "flex", alignItems: "center", justifyContent: "center" }}>
        <Container maxWidth="md" sx={{ mt: 4, mb: 4 }}>
          <Router>
            <Routes>
              <Route path="/" element={<Login />} />
              <Route path="/signup" element={<Signup />} />
              <Route path="/dashboard" element={<PrivateRoute element={<Dashboard />} />} />
              <Route path="*" element={<Typography variant="h4" align="center">404: Page Not Found</Typography>} />
            </Routes>
          </Router>
        </Container>
      </Box>
    </ThemeProvider>
  );
};

export default App;
