// import logo from './logo.svg';
// import './App.css';
// Improved React UI with Modern Design & Responsive Styling
// Applying Material-UI Theme & Tailwind CSS

import React from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { ThemeProvider, createTheme } from "@mui/material/styles";
import { CssBaseline, AppBar, Toolbar, Typography, Container } from "@mui/material";
import Login from "./Login";
import Signup from "./Signup";
import Dashboard from "./Dashboard";

const theme = createTheme({
  palette: {
    mode: "light",
    primary: {
      main: "#007bff",
    },
    secondary: {
      main: "#ff4081",
    },
    background: {
      default: "#f4f6f8",
    },
  },
  typography: {
    fontFamily: "Roboto, sans-serif",
  },
});

const PrivateRoute = ({ element }) => {
  const token = localStorage.getItem("token");
  return token ? element : <Navigate to="/" replace />;
};

const App = () => {
  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6">Academic Platform</Typography>
        </Toolbar>
      </AppBar>
      <Container>
        <Router>
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/dashboard" element={<PrivateRoute element={<Dashboard />} />} />
            <Route path="*" element={<Typography variant="h4">404: Page Not Found</Typography>} />
          </Routes>
        </Router>
      </Container>
    </ThemeProvider>
  );
};

export default App;
